#include<iostream>
#include<map>
using namespace std;
int main() {
    map<int, int>Map;
    int n, m,num;
    cin >> n >> m;
    map<int, int>::iterator l_it;;
    for (int i = 0; i < n; i++) {
        cin >> num;
        l_it = Map.find(num);
        if (l_it == Map.end()) {
            Map.insert(pair <int, int>(num, i + 1));
        }
        else {
            Map[num] = i + 1;
        }
    }
    for (int i = 0; i < m; i++) {
        cin >> num;
        l_it = Map.find(num);
        if (l_it == Map.end()) {
            cout << 0 << endl;
        }
        else {
            cout << l_it->second << endl;
        }
    }
}

