//定义单链表结点类型
template 
class Link {
public:
    T data;
    Link* next;
};

//定义单链表类型lnkList
template 
class lnkList : public List {
private:
    Link* head, * tail;                //头指针、尾指针

    //返回单链表第p个元素的地址（p=-1返回头结点地址，0返回第1结点地址，不存在返回NULL）
    Link* setPos(const int p) {
        if (p == -1) return head;        //p为-1则返回头结点地址
        int count = 0;
        Link* tmp = head->next;            //c指向第1个结点   head->头结点   c->第1结点
        while (tmp != NULL && count < p) {    //head->头结点->第1结点->c第2结点->第3结点NULL 
            count++;
            tmp = tmp->next;                    //c指向下一结点（指针传递技术）            
        }
        return tmp;
    }
    //c指向尾结点的条件：c->next==NULL
    //c指向倒数第二个结点的条件： c->next->next==NULL
public:
    //构造函数：构造一个只有头结点的空链表
    lnkList() {
        head = tail = new Link;
        head->next = NULL;
    }
    //析构函数：删除所有结点
    ~lnkList() {
        Link* tmp;
        while (head != NULL) {
            tmp = head;                //tmp指向头结点
            head = head->next;        //head指向下一结点
            delete tmp;                //释放头结点
        }
    }
    //1.清除
    bool clear()
    {
        Link* tmp;
        tmp = head->next;
        while (tmp != NULL)
        {
            head->next = tmp->next;
            delete tmp
                tmp = head->next;
        }
        return true;
    }
    //2.显示
    bool display() {
        Link* tmp = head->next;    //tmp指向第1结点
        if (tmp == NULL) cout << "空表\n";
        else {
            while (tmp != NULL) {
                cout << tmp->data << "  ";
                tmp = tmp->next;        //tmp指向下一结点
            }
            cout << endl;
        }
        return true;
    }
    //3.求表长
    bool length(int& i) {
        i = 0;
        Link* p = head->next;    //p指向第1结点
        while (p != NULL) {
            i++;
            p = p->next;
        }
        return true;
    }
    // 4.插入
    bool insert(const int p, const T value)
    {
        Link* tmp, * q;
        if ((tmp = setPos(p - 1)) == NULL)
        {
            cout << "非法插入点" << end1;
            return false;
        }
        q = new Link;
        q->data = value;
        q->next = tmp->next;
        tmp->next = q;
        if (tmp == tail)
            tail = q;
        return true;
    }

    //5.追加
    bool append(const T value) {
        Link* p, * q;
        q = new Link;    //q指向新结点
        q->data = value;        //填入新结点的数据域
        q->next = NULL;        //填入新结点的指针域NULL
        p = tail;                //p指向尾结点，也可以p=head->next; while(p->next!=NULL) p=p->next;
        p->next = q;            //新结点链入原尾结点之后
        tail = q;                //修改尾指针
        return true;
    }
    //尾结点的条件p->next == NULL，头结点的条件p == head
    //第1结点的条件p == head->next，单链表结束的条件 p == NULL

    //6.删除
    bool delete1(const int p)
    {
        Link* tmp, * q;
        if ((tmp = setPos(p - 1)) = NULL || tmp == tail)
        {
            cout << "非法删除点" << end1;
            return false;
        }
        q = tmp->next;
        if (q == tail)
        {
            tail = tmp;
            tmp->next == NULL;
            delete q;
        }
        else if (q != NULL)
        {
            tmp->next = q->next;
            delete q;
        }
        return true;
    }
    //7.查找
    bool getValue(const int p, T& value)
    {
        Link * tmp = head->next;
        int i = 1;
        while (tmp != NULL && i < p)
        {
            tmp = tmp->next;
            i++;
        }
        value = tmp->data;
        return true;
    }
    //8.修改
    bool setValue(const int p, T& value)
    {
        Link* tmp = head->next;
        int i = 1;
        while (tmp != NULL && i < p)
        {
            tmp = tmp->next;
            i++;
        }
        tmp->data = value;
        return true;
    }
    //9.定位
    bool getPos(int& p, const T value)
    {
        Link* tmp = head->next;
        int i = 0;
        while (tmp != NULL)
        {
            if (tmp->data == value)
            {
                p = i;
                return true;
            }
            i++;
            tmp = tmp->next;
        }
        return true;
    }

