//插值查找(按比例查找)
int bia_search(int str[], int n, int key)
{
    int low = 0, high = n - 1;
    int mid = 0;
    while (low <= high)
    {
        //折半与按比例的优劣，数的值跨度不是很大按比例更好，
        mid = low + (key - str[low]) / (str[high] - str[low]) * (high - low);
        if (str[mid] == key)
            return mid;
        if (str[mid] > key)
            high = mid - 1;
        if (str[mid] < key)
            low = mid + 1;
    }
    return -1;

}
