void produceFib(int* fib, int size)
{
    int i;
    fib[0] = 0;
    fib[1] = 1;
    for (i = 2; i < size; i++)
    {
        fib[i] = fib[i - 1] + fib[i - 2];
    }
}


int FibonacciSearch(int* data, int length, int searchValue)
