    int top;
public:
    Stack(int w);  
    bool isempty();     
    bool isfull();
    bool push(const T& x);
    bool pop(T x);
    ~Stack(){delete[] element;}
};

template
Stack::Stack(int w)
{
    top = -1;
    maxSize = w;
    element = new T(w);
}

template 
bool Stack::isempty()
{
    return top == -1;
}

template 
bool Stack::isfull()
{
    return top+1 == maxSize;
}

template 
bool Stack::push(const T& x)
{
    if(isfull())
        return false;

    else
    {
        element[top + 1] = x;
        top++;
        return true;
    }
}

template 
bool Stack::pop(T x)
{
    if(isempty())
        return false;
    else
    {
        x = element[top];
        top--;
        return true;
    }
}
#endif
