const int MaxSize = 10;    //10只是示例性的数据，可以根据实际问题具体定义
template 
class SeqList
{
public:
    SeqList() { length = 0; }       //无参构造函数，创建一个空表
    SeqList(T a[], int n);       //有参构造函数
    void Insert(int i, T x);   //在线性表中第i个位置插入值为x的元素
    T Delete(int i);        //删除线性表的第i个元素
    void PrintList();      //遍历线性表，按序号依次输出各元素
    int Locate(T x);
private:
    T data[MaxSize];      //存放数据元素的数组
    int length;            //线性表的长度
};

template 
SeqList::SeqList(T a[], int n)
{
    if (n > MaxSize) throw "参数非法";
    for (int i = 0; i < n; i++)
        data[i] = a[i];
    length = n;
}

template
void SeqList::Insert(int i, T x)
{
    if (length >= MaxSize) throw "上溢";
    if (i<1 || i>length + 1) throw "位置非法";
    for (int j = length; j >= i; j--)
        data[j] = data[j - 1];   //注意第j个元素存在数组下标为j-1处
    data[i - 1] = x;
    length++;
}

template
T SeqList::Delete(int i)
{
    if (length == 0) throw "下溢";
    if (i<1 || i>length) throw "位置非法";
    int x = data[i - 1];
    for (int j = i; j < length; j++)
        data[j - 1] = data[j];   //注意此处j已经是元素所在的数组下标
    length--;
    return x;
}

template
void SeqList::PrintList()
{
    for (int i = 0; i < length; i++)
        cout << data[i] << "  ";
    cout << endl;
}


template
int SeqList::Locate(T x)
{
    for (int i = 0; i < length; i++)
    {
        if (data[i] == x)
        {
            return i + 1;
            break;
        }
    }
}



