#include <iostream>
using namespace std;

int Count_Digit(const int N) {
  int sum = 0, n = abs(N);
  while (n) {
    int digit = n % 10;
    if (digit % 2 == 1) sum += digit;
    n /= 10;
  }
  return sum;
}

int main() {
  int N = -31252;
  cout << Count_Digit(N) << endl;
  return 0;
}

