//判断数中是否有重复数字，且不含0和9
int isReapeat(int n)
{
    int a[10],i=0,j,t;
    while(n)
    {
        t=n%10;
        if(t==0 || t==9) return 1; //不能包含0和9
        for(j=0;j<i;j++)
        {
            if(a[j] == t) return 1;
        }
        a[i++]=t;
        n/=10;
    }
    return 0;
}
