        scanf("%d", &a[i]);
    //排序
    for (i = 0; i < n - 1; i++)
    {
        for (j = 0; j < n - 1 - i; j++)
        {
            if (a[j] > a[j + 1]) //从小到大排序
            {
                t = a[j];
                a[j] = a[j + 1];
                a[j + 1] = t;
            }
        }
    }
    if (n % 2 == 1)
        printf("中位数是%d", a[n / 2]); //奇数个时，中位数是中间的值
    else
        printf("中位数是%.2f", (a[n / 2] + a[n / 2 - 1]) / 2.0); //数组元素偶数个时，中位数是中间两个数的平均值，保留2位小数
    printf("\n");
}


