//1234%10 = 4,123%10 = 3,12%10 = 2
// %10 可以取到数字最后一位
// /10 可以取到数字的前几位
int is(int number){
    int sum = 0,t = 0;
    while (number != 0)
    {
        t = number%10;
        sum += t;
        number /= 10;
    }
    if(sum == 5){
        return 1;
    }
    return 0;
}


void count_sum( int a, int b ){
    int count=0,sum=0;
    for(int i=a;i <= b;i++){
        if(is(i) != 0){
            count += 1;
            sum += i;
        }
    }
