#include <stdio.h>

int ArrayShift(int a[], int n, int m)
{
    int i;
    int k;
    if (m >= n)
        m = m % n;
    for (k = 1; k <= m; k++)
    {
        int temp = a[n - 1];
        for (i = n - 1; i > 0; i--)
        {
            a[i] = a[i - 1];
        }
        a[0] = temp;
    }
}

