        if (a[i] == 9999)
            break;
        i++;
    }
    n = i;
    scanf("%d", &m);
    ArrayShift(a, n, m);
    for (i = 0; i < n; i++)
    {
        if (i != 0)
            printf(" ");
        printf("%d", a[i]);
    }
    printf("\n");

    return 0;
}


