//冒泡排序
void bubble_sort(int a[],int n)
{
    int i,j,t;
    for (i=0;i<n-1;i++)
    {
        for (j=0;j<n-1-i;j++)
        {
            if(a[j] > a[j+1])  //从小到大，升序
            {
                t = a[j];
                a[j]=a[j+1];
                a[j+1]=t;
            }
        }
    }
    return ;
}
//判断是否是水仙花
int isSxh(int n,int p[],int *ws)
{
    int s = 0;
    int a = n;
    int d = 0;
    int i = 0;
    while(a>0)
    {
        int d = a%10;
        p[i] = d;
        s += (d*d*d);
        a = a/10;
        i++;
    }
    *ws = i;
    if(s == n)
        return 1;
    else
        return 0;
}
int main()
{
    int n,s=0,big=0;
    int p[20],i,ws=0;
