    if(!isSxh(n,p,&ws)) //不是水仙花数
    {
        printf("不是水仙花数\n");
        return 0;
    }
    //处理水仙花数
    bubble_sort(p,ws); //所有数按照从小到大排序

    if(ws==3)
    {
        printf("%d ",p[0]*100+p[1]*10+p[2]);
        printf("%d ",p[0]*100+p[2]*10+p[1]);
