#include <stdio.h>
#include <ctype.h>

int main()
{
    char password[80];
    int i = 0;
    char c;
    while ((c = getchar()) != EOF)
    {
        if (c == '\n')
            break;
        if (!isalpha(c) && !isdigit(c))
        {
            printf("密码不符合规范\n");
            return -1;
        }
        char ch;
        if (c == '9')
            ch = 'X';
        else if (isdigit(c))
            ch = c + 1;
        else if (c >= 'a' && c <= 'z')
            ch = 'A' + (c - 'a' + 3) % 26;
        else if (c >= 'A' && c <= 'Z')
            ch = 'a' + (c - 'A' + 3) % 26;
        password[i++] = ch;
    }
    password[i] = '\0';
    if (i < 6)
    {
        printf("密码过短\n");
        return -2;
    }
    else if (i > 10)
    {
        printf("密码过长\n");
        return -3;
    }
    else
    {
        printf("%s\n", password);
        return 0;
    }
}

