思路是定义一个y行x列的二维数组,遍历数组找出值为1(白子)的元素,再看这个元素右侧连续的元素是否也是白子,并且这个白子下方也是连续有与横向白子数加1的白子。有则计数加1。
#include <bits/stdc++.h>
using namespace std;
int main(){
    int x,y,count=0,i,j,k,l,f;
    cin >> y >> x;
    int a[y][x];
    for (int i = 0; i < y; i++)
        for (int j = 0; j < x; j++)
            cin >> a[i][j];

    for (i = 0; i < y-2; i++)
    {
        for (j = 0; j < x-1; j++)
        {
            if (a[i][j]==1)
            {
                for (k = j+1; k < x; k++)
                {
                    if (a[i][k]==0)
                        break;
                    f = k - j + 1;
                    for (l = i+1; l < y && f > 0; l++)
                    {
                        if (a[l][k]==0)
                            break;
                        f--;
                    }
                    if (f==0)
                        count++;
                }
            }
        }
    }
    cout << count << endl;
    return 0;
}



