#include<iostream>
using namespace std;
class complex{
    private:
        int real;
        int image;
    public:
        complex(){}
        complex(int real,int image){
            this->real=real;
            this->image=image;
        }
        void setReal(int value){
            real=value;
        }
        void setImage(int value){
            image=value;
        }
        int getReal()const{
            return real;
        }
        int getImage()const{
            return image;
        }
        void display(){
            cout<<real<<'+'<<image<<'i'<<endl;
        }

        complex operator+(const complex &a)const{
            complex b;
            b.setReal(real+a.getReal());
            b.setImage(image+a.getImage());
            return b;
        }
        complex operator+(const int &a)const{
            complex b;
            b.setReal(real+a);
            b.setImage(image);
            return b;
        }
        //定义非成员函数operator+
        friend complex operator+(int a,const complex &b);
};
complex operator+(int a,const complex &b){
    complex c;
    c.setReal(a+b.getReal());
    c.setImage(b.getImage());
    return c;
}

int main(){
    complex a(1,2);
    complex c;
    complex d;
    c=a+2;
    c.display();
    d=4+a;
    d.display();
    return 0;
}



