#include <iostream>
using namespace std;
class Complex
{
public:
    int real;
    int image;
public:
    Complex(int r, int i)
    {
        real = r; image = i;
    }
    Complex(int r)
    {
        real = r; image = 0;
    }
    Complex()
    {
        real = 0; image = 0;
    }

    ~Complex()
    {
        //do nothing
    }
    Complex operator *= (const Complex Right) 
    {
        int a = real, b = image;
        real = a * Right.real - b * Right.image;
        image = b * Right.real + a * Right.image;
        return *this;
    }

    Complex operator *= (const int n) 
    {
        Complex t(n, 0);
        *this *= t;
        return *this;
    }
    friend void print(Complex comp);

};


void print(Complex comp)
{
    cout << comp.real;
    if (comp.image < 0)
        cout << comp.image << "i" << endl;
    else if (comp.image > 0)
        cout <<"+" << comp.image << "i" << endl;
}

