#include<stdio.h>

int main()
{
	int a;
	int b;
	char operation;
	int num;

	while (scanf("%d", &a) != EOF)
	{
		scanf("%c", &operation);
		scanf("%d", &b);

		if (operation == '+')
		{
			num = a + b;
			printf("%d\n", num);
		}
		else if (operation == '-') {
			num = a - b;
			printf("%d\n", num);
		}
		else if (operation == '*') {
			num = a * b;
			printf("%d\n", num);
		}
		else if (operation == '/' && b != 0) {
			num = a / b;
			printf("%d\n", num);
		}
		else {
			printf("%s\n", "WA");
		}
	}
	return 0;
}

