        ListNode* prev=res;
        int jw = 0;
        ListNode* p1 = l1;
        ListNode* p2 = l2;
        //ListNode* p = nullptr;
        while (p1 != nullptr && p2 != nullptr) {
            ListNode* ptmp;
            int n = p1->val + p2->val + jw;
            jw = n / 10;
            n = n % 10;
            ptmp = new ListNode(n);
            prev->next=ptmp;
            prev=ptmp;
            p1 = p1->next;
            p2 = p2->next;
        }
        while (p1 != nullptr)
