        while (p2 != nullptr) {
            int n = p2->val + jw;
            jw = n / 10;
            n = n % 10;
            ListNode* ptmp = new ListNode(n);
            prev->next = ptmp;
            prev = ptmp;
            p2 = p2->next;
        }
        if (jw > 0) {
            ListNode* ptmp = new ListNode(jw);
            prev->next = ptmp;
        }
        return res->next;


