int main()
{    
    double f[30][20] = {0}, d;
    int a[30] = {0}, i, j, k, m, n, r, c[30][20] = {0}, b[20];
    char l[30];
    scanf("%d", &r);
    scanf("%s",l);


    n = 0;
    while(l[n] != '\0'){
        //字符转换成数字
        a[n] = l[n] - 48;
        n++;
    }

    //边界初始化
    for (i = 0; i < n - r; i++)
    {
        for (d = 0, j = 0; j <= i; j++)
            d = d * 10 + a[j];
        f[i][0] = d;    
    }

    //递推
    for (j = 1; j <= r; j++)
    {
        for (i = j; i < n - r + j; i++)
        {
            for (k = j; k < i; k++)
            {
                for (d = 0, m = k + 1; m <= i; m++)
                    d = d * 10 + a[m];

                if (f[i][j] < f[k][j-1] * d)
                {
                    f[i][j] = f[k][j-1] * d;
                    c[i][j] = k;
                }
            }
        }
    }

    //结果输出
    j = r;
    i = n - 1;
    while (j > 0)
    {
        b[j-1] = c[i][j];
        i = c[i][j];
        j--;
    }

    for (i = 0, j = 0, k = 0; k <= r; k++)
    {
        for (m = j; m <= (k == r ? n - 1 : b[k]); m++)
        {
            printf("%d", a[m]);
        }
        if (k != r) printf("×");
        j = b[k] + 1;
    }

    printf("=%d", (int)f[n-1][r]);

    return 0;
}

