package org.suanfa.test;



import java.io.BufferedReader;

import java.io.IOException;

import java.io.InputStreamReader;



public class Test1 {

 private boolean isPositive=true;

 private String number="0";

 public Test1() {



}

 public Test1(String number) {

  String flag=number.substring(0, 1);

  if(flag.equals("-")){

   this.isPositive=false;

   this.number=number.substring(1);

  }else{

   this.isPositive=true;

   this.number = number;

  }

 }

private int compare(String a,String b){

  int MaxLen=0;

  if(a.length()>=b.length()){

   MaxLen=a.length();

   while(b.length()
    b="0"+b;

   }

  }else{

   MaxLen=b.length();

   while(a.length()
    a="0"+a;

   }

  }

  if(a.compareTo(b)>0)

   return 1;

  else if(a.compareTo(b)==0)

   return 0;

  else

   return -1;

 }



public Test1 add(Test1 bigint){

  Test1 bi=new Test1();

  String a=this.number;

  String b=bigint.number;

  if((this.isPositive&&bigint.isPositive)||(!this.isPositive&&!bigint.isPositive)){//同符号

   bi.isPositive=this.isPositive;

   bi.number=absAdd(a,b);

  }else{

   if(compare(a,b)>0){

bi.isPositive=this.isPositive;

    bi.number=absSub(a,b);

   }else if(compare(a,b)==0){

    bi.isPositive=true;

    bi.number="0";

   }else{

bi.isPositive=bigint.isPositive;

    bi.number=absSub(a,b);

   }

  }

  return bi;

 }



public Test1 sub(Test1 bigint){

  Test1 bi=new Test1();

  String a=this.number;

  String b=bigint.number;

  if((this.isPositive&&bigint.isPositive)||(!this.isPositive&&!bigint.isPositive)){

   if(compare(a,b)>0){

    bi.isPositive=this.isPositive;

    bi.number=absSub(a,b);

   }else if(compare(a,b)==0){

     bi.isPositive=true;

     bi.number="0";

   }else{

    bi.isPositive=!bigint.isPositive;

    bi.number=absSub(a,b);

   }

  }else{

   bi.isPositive=this.isPositive;

   bi.number=absAdd(a,b);

  }

  return bi;

 }



public Test1 multi(Test1 bigint){

     Test1 bi=new Test1();

  String a=this.number;

  String b=bigint.number;

  if(a.equals("0")||b.equals("0")){

   bi.isPositive=true;

   bi.number="0";

  }else if((this.isPositive&&bigint.isPositive)||(!this.isPositive&&!bigint.isPositive)){

   bi.isPositive=true;

   bi.number=absMulti(a,b);

  }else{

   bi.isPositive=false;

   bi.number=absMulti(a,b);

  }

  return bi;

 }

 public Test1 div(Test1 bigint){

     Test1 bi=new Test1();

  String a=this.number;

  String b=bigint.number;

  if(a.equals("0")||b.equals("0")){

   bi.isPositive=true;

   bi.number="0";

  }else if((this.isPositive&&bigint.isPositive)||(!this.isPositive&&!bigint.isPositive)){

   bi.isPositive=true;

   bi.number=absDiv(a,b);

  }else{

   bi.isPositive=false;

   bi.number=absDiv(a,b);

  }

  return bi;

 }

 private String absAdd(String a, String b) {

  int MaxLen=0;

  if(a.length()>=b.length()){

   MaxLen=a.length();

   while(b.length()
    b="0"+b;

   }

  }else{

   MaxLen=b.length();

   while(a.length()
    a="0"+a;

   }

  }

  String str = "";

  int JW = 0;      // 进位

  for (int i = MaxLen - 1; i >= 0; i--) {

   int tempA = a.charAt(i)-'0';

   int tempB = b.charAt(i)-'0';

   int temp;

   if (tempA + tempB + JW >= 10 && i != 0) {          //如果有进位

    temp = tempA + tempB + JW - 10;

    JW = 1;

   } else {

    temp = tempA + tempB + JW;

    JW = 0;

   }

   str = String.valueOf(temp) + str;

  }

  while(str.length()>1&&str.charAt(0)=='0'){

   str=str.substring(1);

  }

  return str;

 }

  private String absSub(String a, String b) {

  int MaxLen=0;

  if(a.length()>=b.length()){

   MaxLen=a.length();

   while(b.length()
    b="0"+b;

   }

  }else{

   MaxLen=b.length();

   while(a.length()
    a="0"+a;

   }

  }

  if(a.compareTo(b)
   String temp=a;

   a=b;

   b=temp;

  }

  String str = "";

  int JW = 0;          // 借位

  for (int i = MaxLen - 1; i >= 0; i--) {

   int tempA = a.charAt(i)-'0';

   int tempB = b.charAt(i)-'0';

   int temp;

   if (tempA - JW - tempB < 0 && i != 0) {

    temp = tempA + 10 - JW - tempB;

    JW = 1;

   } else {

    temp = tempA - JW - tempB;

    JW = 0;

   }

   str = temp + str;

  }

  while(str.length()>1&&str.charAt(0)=='0'){

   str=str.substring(1);

  }

  return str;

 }

  private String absMulti(String a,String b){

  String result="0";

  if(a.equals("0")||b.equals("0"))

