     for(int i = b.length()-1; i >=0; i--){

      int plustimes=b.charAt(i)-'0';

      String atemp=a;

      for(int j=1;j<=b.length()-1-i;j++){

       atemp=atemp+"0";

      }

      for(int m=1;m<=plustimes;m++){

       result=absAdd(result,atemp);

      }

     }

  return result;

 }

  private String absDiv(String a,String b){

  if(a.equals("0")||b.equals("0"))

