} ys[5];

const int arr[15] = { 1868, 9, 8, 1912, 7, 30, 1926, 12, 25, 1989, 1, 8, 2019, 5, 1 };

int main(int argc, char *argv[])
{
    // 初始化日本五个纪年日期
    for (int i = 0, j = 0; i < 5; i++)
    {
        ys[i].year = arr[j++];
        ys[i].mon = arr[j++];
        ys[i].mday = arr[j++];
    }
    char ch;
    int y, m, md, n;
    scanf("%c%d/%d/%d%d", &ch, &y, &m, &md, &n);
    switch (ch)
    {
    case 'M':
        y += ys[0].year - 1;
        break;
    case 'T':
        y += ys[1].year - 1;
        break;
    case 'S':
        y += ys[2].year - 1;
        break;
    case 'H':
        y += ys[3].year - 1;
        break;
    case 'R':
        y += ys[4].year - 1;
        break;
    default:
        puts("输入有误！");
        return -1;
    }
    time_t t, tim[5];
    struct tm tt, tt2[5];
    tt.tm_year = y - 1900;
    tt.tm_mon = m - 1;
    tt.tm_mday = md + n;
    tt.tm_hour = 23;
    tt.tm_min = 0;
    tt.tm_sec= 0;

    // 转成秒
    t = mktime(&tt);

    for (int i = 0; i < 5; i++)
    {
        tt2[i].tm_year = ys[i].year - 1900;
        tt2[i].tm_mon = ys[i].mon - 1;
        tt2[i].tm_mday = ys[i].mday;
        tt2[i].tm_hour = 0;
        tt2[i].tm_min=0;
        tt2[i].tm_sec=0;
        tim[i] = mktime(&tt2[i]);
    }
    // 重新输出具体日期
    char no;
    // t tim
    if (t >= tim[4])
    {
        no = 'R';
        tt.tm_year += -118;
    }
    else if (t >= tim[3] && t < tim[4])
    {
        no = 'H';
        tt.tm_year += -88;
    }
    else if (t >= tim[2] && t < tim[3])
    {
        no = 'S';
        tt.tm_year += -25;
    }
    else if (t >= tim[1] && t < tim[2])
    {
        no = 'T';
        tt.tm_year += -11;
    }
    else if (t < tim[1])
    {
        no = 'M';
        tt.tm_year += 33;
    }
    // 输出
    printf("%c%d/%d/%d", no, tt.tm_year, tt.tm_mon + 1, tt.tm_mday);
    return 0;
}

