#include <stdio.h>
#define ROWS 9
#define COLS 9

int main() {
    int array[ROWS][COLS];
    int i, j;

    // 计算乘积并将结果存入二维数组中
    for (i = 0; i < ROWS; i++) {
        for (j = 0; j < COLS; j++) {
            array[i][j] = (j + 1) * (i + 1); // 先计算大数*j，再计算小数*i
        }
    }

    // 打印九九乘法表
    printf("    九九乘法表\n");
    printf("    ");
    for (i = 1; i <= COLS; i++) {
        printf("%2d ", i);
    }
    printf("\n");

    for (i = 0; i < ROWS; i++) {
        printf("%2d  ", i+1);
        for (j = 0; j < COLS; j++) {
            printf("%2d ", array[i][j]);
        }
        printf("\n");
    }

    return 0;
}

