#include <stdio.h>

int main() {
    int array[9][9];

    printf("    九九乘法表\n");

    // 计算乘法表并存储在二维数组array中
    for (int i = 1; i <= 9; i++) {
        for (int j = 1; j <= 9; j++) {
            array[i-1][j-1] = i * j;
        }
    }

    // 输出乘法表
    for (int i = 1; i <= 9; i++) {
        for (int j = 1; j <= 9; j++) {
            if (i <= j) {
                printf("%d × %d = %2d  ", i, j, array[i-1][j-1]);
            }
        }
        printf("\n");
    }

    return 0;
}

