#define N 100
using namespace std;
int Lengh(char a[])
{
    int i=0,j=0;
    while(a[i]!='\0')
    {
        if(a[i]!=' ')
            j++;
        i++;
    }
    return j;
}
void mul(char int1[],char int2[],int n)
{
    char *sum = new char[2*(n+1)];
    char *temp = new char[2*(n+1)];
    printf("%*s\n",2*(n+1),int1);
    printf("%*s\n",2*(n+1),int2);
    printf("%*s",n+1,"X ");
    for(int i = 0; i < n+1; i++)
        printf("-");
    printf("\n");

    for(int i = 0; i < 2*(n+1); i++) {
        sum[i] = '0';
        temp[i] = '0';
    }
    for(int i = n; i > -1; i--) {
        if(int2[i]=='1')
        {
            for(int k = 0; k < 2*(n+1); k++)
            {
                temp[k] = '0';
            }
            for(int j = n; j > -1; j--)
            {
                temp[j+i+1] = int1[j];
            }
            bool carry = false;
            for(int i = (2*n + 1); i > -1; i--)
            {
                if(!carry)
                {
                    if((sum[i]^temp[i])==0)
                    {
                        if((sum[i]=='1')&&(temp[i]=='1'))
                        {
                            carry = true;
                        }
                        sum[i] = '0';
                    }
                    else
                    {
                        sum[i] = '1';
                    }
                } else
                {
                    if((sum[i]^temp[i])==0)
                    {
                        carry = ((sum[i]=='1')&&(temp[i]=='1')) ? true : false;
                        sum[i] = '1';
                    }
                    else
                    {
                        sum[i] = '0';
                        carry = true;
                    }
                }
            }
            printf("%*s\n",n+2+i,int1);
        }
        else
        {
            printf("%*s",i+1,"");
            for(int k = 0; k < n+1; k++)
                printf("0");
            printf("\n");

        }


    }
    for(int i = 0; i < (n+1); i++)
    {
        int1[i] = sum[i];
    }
    for(int i = (n+1); i < 2*(n+1); i++)
    {
        int2[i-n-1] = sum[i];
    }
    for(int i = 0; i < 2*(n+1); i++)
        printf("-");
    printf("\n");
    printf("%s\n",sum);

}





