    a=Lengh(c);
    b=Lengh(d);
    if(a!=b)
    {
        cout<<"输入的二进制数不正确，两数长度不匹配"<<endl;
        goto LOOP;
    }
    a=a-1;
    mul(c,d,a);
    goto LOOP;
}


