#include<iostream>
#include<vector>
#include<string>
#include<cstring>
using namespace std;
//信息太少，先假设迷宫最多100层
const int SIZE = 100;
string  mg[SIZE];
int book[SIZE][SIZE] = { 0 };
int v[4][2] = { {0,1},{0,-1},{1,0},{-1,0} };

void dfs(int m,int n,vector<char>& road,int i,int j, vector<vector<char>> &ans)
{
    //到达终点
    if (i == m && j == n)
    {
        ans.push_back(road);
    }
    else
    {
        for (int k = 0; k < 4; k++)
        {
            //上下左右走，并标记走过的位置
            int vi = i + v[k][0];
            int vj = j + v[k][1];
            if (vi >= 0 && vi <= m && vj >= 0 && vj <= n && book[vi][vj] == 0&&mg[vi][vj]=='0')
            {
                switch (k)
                {
                case 0:road.push_back('R'); break;
                case 1:road.push_back('L'); break;
                case 2:road.push_back('D'); break;
                case 3:road.push_back('U'); break;
                default:
                    break;
                }
                book[vi][vj] = 1;
                dfs(m, n, road, vi, vj, ans);
                //回溯
                book[vi][vj] = 0;
                road.pop_back();
            }
        }
    }

}
int main()
{
    //读入地图
    int i;
    for (i = 0; i < SIZE; i++)
    {
        string temp;
        getline(cin, temp);
        if (temp.empty())
        {
            break;
        }
        mg[i] = temp;
    }
    vector<char> road;
    vector<vector<char>> ans;
    dfs(i-1, mg[0].length()-1, road, 0, 0,ans);
    if (ans.size())
    {
        cout << ans.size() << endl;
    }
    else
    {
        cout << "NO PASS!" << endl;
        return 0;
    }

    for (int i = 0; i < (int)ans.size(); i++)
    {
        for (int j = 0; j < (int)ans[i].size(); j++)
        {
            cout << ans[i][j];
        }
        cout << endl;
    }
}

