public static void main(String[] args) throws Exception {
        File tmpDir = new File("你的文件路劲");
        for (File f : tmpDir.listFiles()) {
            String name = f.getName();
            if (name.endsWith(".json")) {
                BufferedReader bufferedReader = new BufferedReader(new FileReader(f));
                String st;
                StringBuilder fileContent = new StringBuilder();
                while ((st = bufferedReader.readLine()) != null) {
                    fileContent.append(st);
                }
                bufferedReader.close();
                ObjectMapper objectMapper = new ObjectMapper();
                Map json = objectMapper.readValue(fileContent.toString(), Map.class);
                setFilePath(json, f.getAbsolutePath());
                // 修改后的json，然后重新写入文件
                String result = objectMapper.writeValueAsString(json);
                FileWriter fileWriter = new FileWriter(f);
                fileWriter.write(result);
                fileWriter.close();
            }
        }
    }

    // 递归遍历, 可能存在多个fileName字段
    public static void setFilePath(Map json, String filePath) {
        json.forEach((k, v) -> {
            if (k instanceof Map) {
                setFilePath((Map) k, filePath);
            }
            if ("fileName".equals(k)) {
                json.put("fileName", filePath);
            }
        });
    }

