int fac(int x)
{
    if (x<=1)
        return 1;
    return x * fac(x-1);
}
int main()
{
    int n,m;
    for (n = 0; n <5 ; n++)
    {
        for (m = 0; m <= n; m++)
        {
            int s = fac(n) / fac(m) / fac(n-m);
            printf("%d ", s);
        }
        printf("\n");

    }
    return 0;
}




