public class login {
    public String userName;  //用户名
    public String userPassword;  //密码
    private String strSql;
    private Connection dbConn;
    public login(){
        userName="";
        userPassword="";
        strSql="";
        dbConn=null;
    }
    public boolean isValidUser(){
        strSql="select * from userLogin ";
        strSql=strSql+" where name='"+userName+"'";
        strSql=strSql+" and password='"+userPassword+"'";
        Statement stmt=null;
        ResultSet rs=null;
        try{
            stmt=dbConn.createStatement();
            rs=stmt.executeQuery(strSql);
            if(rs.next()){
                return true;
            }
            else {
                return false;
            }
        }
        catch(Exception ex){
            return false;
        }

    }

    public void setConnection(Connection dbConn){  //得到连接
            this.dbConn = dbConn;
    }
}

