#include<stdio.h>
#include<stdlib.h>

#define maxsize 1000
typedef struct data
{
    int x;
    int y;
}DATA;

int ds[4][2] = {{-1,0},{1,0},{0,-1},{0,1}};
int m,n,len=0;
int a[100][100];
DATA p[maxsize];

int dfs(int x, int y,int x2, int y2) {
    p[len].x = x;
    p[len].y = y;
    len++;
    a[x][y] = 2;
    if (x == x2 && y == y2) {
        return 1;
    }
    for (int i = 0; i < 4; i++) {
        int tx = x + ds[i][0];
        int ty = y + ds[i][1];
        if (a[tx][ty]==0 && tx >= 0 && tx < m && ty >= 0 && ty < n) {
            if (dfs(tx, ty, x2, y2))
                return 1;
        }
    }
    a[x][y] = 0;
    len--;
    return 0;
}
int main()
{
    int x1,y1,x2,y2;
    int i,j;
    scanf("%d %d",&m,&n);
    scanf("%d %d %d %d",&x1,&y1,&x2,&y2);
    for(i=0;i<m;i++)
    {
        for(j=0;j<n;j++)
            scanf("%d",&a[i][j]);
    }
    if (dfs(x1,y1,x2,y2))
    {
        for(i=0;i<len;i++)
        {
            printf("(%d,%d) ", p[i].x, p[i].y);
        }
    }
    else
    {
        printf("No Path!");
    }
    for(i=0;i<m;i++)
    {
        for(j=0;j<n;j++)
            printf("%d ",a[i][j]);
        printf("\n");
    }

    return 0;
}

