package com.mask.demo_A;

import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;
import java.util.HashMap;

public class FilePreprocess {

    public static void preprocess(File file, String outputDir) {
        try {
            splitToSmallFiles(charactorProcess(file, outputDir + "output.all"),
                    outputDir);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    /**
     * 对文件中的字符进行全角/半角转换
     * 
     * @param file
     *            原始文件
     * @param destFile
     *            目标地址
     * @return
     */
    public static File charactorProcess(File file, String destFile) {
        try {
            BufferedWriter writer = new BufferedWriter(new FileWriter(destFile));
            BufferedReader reader = new BufferedReader(new FileReader(file));
            String line = reader.readLine();
            while (line != null) {
                String newLine = replace(line);
                writer.write(newLine);
                writer.newLine();
                line = reader.readLine();
            }
            reader.close();
            writer.close();
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
        }
        return new File(destFile);
    }

    /**
     * 拆分小文件
     * 
     * @param file
     *            源文件
     * @param outputPath
     *            目标文件地址
     * @throws IOException
     */
    public static void splitToSmallFiles(File file, String outputPath)
            throws IOException {
        int filePointer = 0;
        int MAX_SIZE = 10240;
        BufferedWriter writer = null;
        BufferedReader reader = new BufferedReader(new FileReader(file));
        StringBuffer buffer = new StringBuffer();
        String line = reader.readLine();
        while (line != null) {
            buffer.append(line).append("\r\n");
            if (buffer.toString().getBytes().length >= MAX_SIZE) {
                writer = new BufferedWriter(new FileWriter(outputPath
                        + "output" + filePointer + ".txt"));
                writer.write(buffer.toString());
                writer.close();
                filePointer++;
                buffer = new StringBuffer();
            }
            line = reader.readLine();
        }
        writer = new BufferedWriter(new FileWriter(outputPath + "output"
                + filePointer + ".txt"));
        writer.write(buffer.toString());
        writer.close();
    }

    /**
     * 全角/半角转换
     * 
     * @return
     */
    @SuppressWarnings( { "unchecked", "unused" })
    private static String replace(String line) {
        System.out.println("old line = " + line);
        HashMap map = new HashMap();
        map.put("，", ",");
        map.put("。", ".");
        map.put("《", "<");
        map.put("》", ">");
        map.put("‖", "|");
        map.put("〈", "<");
        map.put("〉", ">");
        map.put("〔", "[");
        map.put("〕", "]");
        map.put("？", "?");
        map.put("“", "\"");
        map.put("”", "\"");
        map.put("：", ":");
        map.put("、", ",");
        map.put("（", "(");
        map.put("）", ")");
        map.put("【", "[");
        map.put("】", "]");
        map.put("—", "-");
        map.put("～", "~");
        map.put("！", "!");
        map.put("‘", "'");
        map.put("①", "1");
        map.put("②", "2");
        map.put("③", "3");
        map.put("④", "4");
        map.put("⑤", "5");
        map.put("⑥", "6");
        map.put("⑦", "7");
        map.put("⑧", "8");
        map.put("⑨", "9");
        for (int i = 0; i < line.length(); i++) {
            String charAt = line.substring(i, i + 1);
            if (map.get(charAt) != null) {
                line.replace(charAt, (String) map.get(charAt));
            }
        }
        System.out.println("new line = " + line);
        return line;
    }

    @SuppressWarnings("static-access")
    public static void main(String[] args) throws Exception {
        String inputFile = "f:\\book.txt";
        String outputDir = "f:\\temp\\";
        if (!new File(outputDir).exists()) {
            new File(outputDir).mkdir();
        }
        FilePreprocess process = new FilePreprocess();
        process.preprocess(new File(inputFile), outputDir);
    }
}





