                    unset($schedulesAvailable[$key]);
                    continue;
                }
            } else {
                if(isset($schedulesAvailable[$key])) {
                    $schedulesAvailable[$key] = $schedule;
                }
            }
        }
    } else {
        $schedulesAvailable = $schedules;   
    }       

    return $schedulesAvailable;
}

