#define _CRT_SECURE_NO_WARNINGS 1
#include <stdio.h>
void cpy(char* str1, char* str2)
{
    int i = 0;
    int j = 0;
    while (str1[i])
    {
        if (str1[i] == 'a' || str1[i] == 'e' || str1[i] == 'i' || str1[i] == 'o' || str1[i] == 'u')
            str2[j++] = str1[i];
        else if (str1[i] == 'A' || str1[i] == 'E' || str1[i] == 'I' || str1[i] == 'O' || str1[i] == 'U')
            str2[j++] = str1[i];
        i++;
    }
    str2[j] = 0;
}

int main()
{
    char str1[80], str2[80];
    printf("请输入字符串：");
    gets(str1);
    cpy(str1, str2);

    printf("元音字母：%s", str2);
    return 0;
}

