template <typename T>
int BST<T>::update_height(Node* node){
    if(node==nullptr)
    {
        return -1;
    }
    else if(node->left==nullptr && node->right==nullptr)
    {
        node->height=0;
        return 0;
    }
    else
    {
        node->height=max(update_height(node->left),update_height(node->right))+1;
        return node->height;
    }
}

