Answer:
#include<cstdio>
#include<iostream>
#include<map>
#include<vector>
#include<cstring>
#include<string>
#include<algorithm>
using namespace std;
const int maxn=35;

char s1[maxn],s2[maxn],s3[maxn];
string dp[maxn][maxn][maxn];    //dp[i][j][k]表示s1的i位之前、s2的j位之前、s3的k位之前的公共子串


int main(){
    cin>>(s1+1)>>(s2+1)>>(s3+1);
    int len1=strlen(s1+1);
    int len2=strlen(s2+1);
    int len3=strlen(s3+1);
    for(int i=0;i<=len1;i++)
        for(int j=0;j<=len2;j++)
            for(int k=0;k<=len3;k++){   //有一个字符换为空，则公共子串为空
                dp[i][j][0]="";
                dp[i][0][k]="";
                dp[0][j][k]="";
            }
    for(int i=1;i<=len1;i++)
        for(int j=1;j<=len2;j++)
            for(int k=1;k<=len3;k++)
            {
                if(s1[i]==s2[j]&&s1[i]==s3[k]){     //当前字符串相同
                    dp[i][j][k]=dp[i-1][j-1][k-1]+s1[i];
                }
                else{                               //如果不相等
                    int l1=dp[i-1][j][k].length();  //去掉三个字符串中的这一位，选中去掉当前字符后能够取得的最大公共序列
                    int l2=dp[i][j-1][k].length();
                    int l3=dp[i][j][k-1].length();
                    if(l1==max(l1,max(l2,l3))){
                        dp[i][j][k]=dp[i-1][j][k];
                    }
                    else if(l2==max(l1,max(l2,l3))){
                        dp[i][j][k]=dp[i][j-1][k];
                    }
                    else {
                        dp[i][j][k]=dp[i][j][k-1];
                    }
                }
            }
    cout<<dp[len1][len2][len3]<<endl;
    return 0;
}

