//
 将树形视图保存到XML文件中
        public void SaveToXml()
        {

//
 创建XML文档对象
            XmlDocument doc = new XmlDocument();

//
 创建根节点
            doc.LoadXml(
""
);
            XmlNode root = doc.DocumentElement;

//
 添加XML声明
            doc.InsertBefore(doc.CreateXmlDeclaration(
"1.0"
, 
"utf-8"
, 
"yes"
), root);

//
 将树形视图转换为XML节点，并添加到XML文档中
            TreeNode2Xml(this.treeView1.Nodes, root);

//
 将XML文档保存到文件中
            xmloper.SaveXml(doc);
        }


//
 将TreeNodeCollection转换为XML节点
        private void TreeNode2Xml(TreeNodeCollection treeNodes, XmlNode xmlNode)
        {
            XmlDocument doc = xmlNode.OwnerDocument;
            foreach (TreeNode treeNode 
in
 treeNodes)
            {
                dict = (Dictionary)treeNode.Tag;

//
 创建XML节点
                XmlNode element = doc.CreateNode(
"element"
, 
"Item"
, 
""
);  
//
创建名为item的xml对象
                XmlAttribute attr = doc.CreateAttribute(
"Title"
);         
                attr.Value = treeNode.Text;                               
//
属性值为treeNode.Text   
                element.Attributes.Append(attr);                          
//
添加名为Title的属性   
                element.AppendChild(doc.CreateCDataSection(dict[
"tag"
])); 
//
将treeNode转换为CDataSection并将其作为子节点添加到element中
                xmlNode.AppendChild(element);                             
//
将element添加到xmlNode子节点中

//
 如果该节点有子节点，则递归调用该方法

if
 (treeNode.Nodes.Count > 
0
)
                {
                    TreeNode2Xml(treeNode.Nodes, element);
                }
            }
        }


