        private void SetNodeBackColor(TreeNodeCollection treeNodes)
        {
            foreach (TreeNode treeNode 
in
 treeNodes)
            {
                dict = (Dictionary)treeNode.Tag;
                treeNode.BackColor = Color.White;  
//
 设置节点背景颜色为白色

if
 (dict[
"tag"
].Equals(
"area"
))   
//
 如果节点的Tag属性值为
"area"
，则将该节点折叠
                {
                    treeNode.Collapse();
                }

if
 (treeNode.Nodes.Count > 
0
)
                {
                    SetNodeBackColor(treeNode.Nodes);   
//
 递归调用SetNodeBackColor方法，对当前节点的所有子节点执行相同的操作
                }
            }
        }

        private void SearchXml(TreeNodeCollection treeNodes,string search)
        {
            foreach (TreeNode treeNode 
in
 treeNodes)
            {

if
