#include<stdio.h>
long arr[21][21][21] = { 0 };
long fun(long a, long b, long c)
{
    if (a <= 0 || b <= 0 || c <= 0)
        return 1;
    else if (a > 20 || b > 20 || c > 20)
    {
        if(arr[20][20][20] == 0)
            arr[20][20][20]= fun(20, 20, 20);
        return arr[20][20][20];
