    else if (a < b && b < c)
    {
        if (arr[a][b][c - 1] == 0)
            arr[a][b][c - 1] = fun(a, b, c - 1);
        if (arr[a][b - 1][c - 1] == 0)
            arr[a][b - 1][c - 1] = fun(a, b - 1, c - 1);
        if (arr[a][b - 1][c] == 0)
            arr[a][b - 1][c] = fun(a, b - 1, c);
        return arr[a][b][c - 1] + arr[a][b - 1][c - 1] - arr[a][b - 1][c];
    }
    else 
