/****************************************************************/
/* int BSTDelete1(BinSearchTree *bt, DataType key)              */
/* 功能:删除二叉排序树中的元素key,方法1                          */
/* 输入参数bt:二叉排序树的根                                 */
/* 输入参数key:要删除的元素                                       */
/* 返回值:成功删除返回1,否则返回0                              */
/* 创建日期:2019-5-21                       Author:Cyber Kaka   */
/****************************************************************/
int BSTDelete1(BinSearchTree *bt, DataType key)
{
    BSTreeNode parent, p, maxpl;
    p = *bt;
    parent = NULL;
    //查找被删除的结点
    while (p != NULL)
    {
        if (p->data == key)
            break;  //查找到了,跳出循环
        if (p->data > key)
            p = p->leftchild;
        else
            p = p->rightchild;
    }//查询结束
    if (p == NULL)
    {
        printf("%d not exist\n", key);
        return 0;
    }
    //只有右子树的情况
    if (p->leftchild == NULL)
    {
        //如果被删除的结点是根结点,那就要修改的是二叉排序树的根
        if (parent == NULL)
            *bt = p->rightchild;
        //检查是左孩子还是右孩子
        else if (parent->leftchild == p)
            parent->leftchild = p->rightchild;
        else
            parent->rightchild = p->rightchild;
    }
    //既有左子树也有右子树
    if (p->leftchild != NULL)
    {
        BSTreeNode parentp; //parentp记录maxpl的父结点
        parentp = p;
        maxpl = p->leftchild;
        //对称遍历中,右侧的总是大的数
        //定位p的左子树中的最大结点maxpl
        while (maxpl->rightchild != NULL)
        {
            parentp = maxpl;
            maxpl = maxpl->rightchild;
        }
        p->data = maxpl->data;  //修改p的数据域为maxpl的值
        if (parentp == p)    //如果maxpl的父结点是p
            p->leftchild = maxpl->leftchild;    //修改p结点的左指针
        else
            parentp->rightchild = maxpl->leftchild; //修改父结点的右指针
        p = maxpl;  //更新p指针为maxpl结点以便删除
    }
    //释放空间
    free(p);
    return 1;
}

/****************************************************************/
/* int BSTDelete2(BinSearchTree *bt, DataType key)              */
/* 功能:删除二叉排序树中的元素key,方法2                          */
/* 输入参数bt:二叉排序树的根                                 */
/* 输入参数key:要删除的元素                                       */
/* 返回值:成功删除返回1,否则返回0                              */
/* 创建日期:2019-5-21                       Author:Cyber Kaka   */
/****************************************************************/
int BSTDelete2(BinSearchTree *bt, DataType key)
{
    //parent记录p的父结点,maxpl记录p的左子树中的关键码最大结点
    BSTreeNode parent, p, maxpl;
    p = *bt;
    parent = NULL;
    //查找被删除的结点
    while (p != NULL)
    {
        if (p->data == key)
            break;  //查找到了,跳出循环
        parent = p; //注意这一句
        if (p->data > key)
            p = p->leftchild;
        else
            p = p->rightchild;
    }//查找结束
    if (p == NULL)
    {
        printf("%d not exist!\n", key);
        return 0;
    }
    //只有右子树的情况
    if (p->leftchild == NULL)
    {
        //删除的是根结点,做特殊处理
        if (parent == NULL)
            *bt = p->rightchild;
        //p是父结点parent的左孩子,则修改父结点的左指针
        else if (parent->leftchild == p)
            parent->leftchild = p->rightchild;
        else
            parent->rightchild = p->rightchild;
    }
    //以上和方法1几乎完全相同

    //有左子树和右子树
    if (p->leftchild != NULL)
    {
        maxpl = p->leftchild;
        //定位左子树中的最大结点maxpl
        while (maxpl->rightchild != NULL)
            maxpl = maxpl->rightchild;
        maxpl->rightchild = p->rightchild;
        if (parent == NULL)
            *bt = p->leftchild;
        //p是父结点parent的左孩子,则修改父结点的左指针
        else if (parent->leftchild == p)
            parent->leftchild = p->leftchild;
        //p是父结点parent的右孩子,则修改父结点的右指针
        else
            parent->rightchild = p->leftchild;
    }
    free(p);    //释放结点p
    return 1;
}

