int resolve(int n, int m) {
	int b = 0, c = 0;
	int temp = m - n;
	int sum = 0;
	  
	if ((temp % 4) > n) return 0;
	
	for (int c = (temp/4); c >= 0; --c) {
		b = temp - (c * 4);
		if (b + c <= n)
			sum += 1;
		else
			break;
	}

	return sum;

}
 
