public class Coin {

    public static int getNums(int n){
   
        List list = new ArrayList();
        
        for(int i=0;i<1000;i++){
            for(int j=0; j < 1000; j++) {
                for(int z=0; z < 1000; z++) {
                    if(25*i+10*j+z==n){
