    public static boolean check(String target, String condition, int targetPos, int targetSame) {
        int[] cnt1 = new int[10];
        int[] cnt2 = new int[10];
        // ä½ç½®æ­£ç¡®ä¸”æ•°å­—æ­£ç¡®ä¸ªæ•°
        int posCnt = 0;
        for (int i = 0; i < 4; i++) {
