            cnt1[c1 - '0']++;
            cnt2[c2 - '0']++;
            // é‡å¤æ•°å­—ç›´æ¥è¿”å›false
            if (cnt1[c1 - '0'] > 1) return false;
            if (c1 == c2) posCnt ++;
        }
