//ç®€å•æ¨¡å¼åŒ¹é…
int Index(SString S, SString T, int pos){
    //è¿”å›  æ¨¡å¼ä¸²T  åœ¨ä¸»ä¸²Sä¸­ç¬¬posä¸ªå­—ç¬¦ä¹‹å çš„ä½ç½®ã€‚å¦‚æœä¸å­˜åœ¨ï¼Œè¿”å›0
    //Téç©º  1<=pos<=S.leng
    int i=pos, j=1;
    while(i<=S.leng && j<=T.leng){
        if(S.data[i]==T.data[j]) {i++;j++;}
        else{i = i-j+2; j=1;}
    }
    if(j>T.leng) return i-T.leng;
    else return 0;
}


//KMPç®—æ³•
int Index_KMP(SString S, SString T, int pos, int next[]){
    //Téç©ºï¼Œ1<=pos<=S.leng
    int i=pos,j=1;
    while(i<=S.leng && j<=T.leng)
        if(j==0||S.data[i]==T.data[j]) {i++;j++;}
        else j = next[j];
    if(j>T.leng) return i-T.leng;
    else return 0;
}
