    while(i<T.leng)
        if(j==0 || T.data[i]==T.data[j]) {i++;j++;next[i]=j;}
        else j = next[j];
}
//nextçš„è®¡ç®—æ–¹æ³•çš„ä¿®æ­£
void get_nextval(SString T, int *nextval){
    int i=1,j=0;
    nextval[1]=0;
    while(i<T.leng)
        if(j==0 || T.data[i]==T.data[j]) {
